    def _print_results(self, undocumented_items: List[Dict[str, Any]], 
                      total_methods: int, documented_methods: int):
        """Ð’Ñ‹Ð²Ð¾Ð´Ð¸Ñ‚ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚Ñ‹ Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸."""
        coverage_percent = (documented_methods / total_methods * 100) if total_methods > 0 else 0

        self.stdout.write('\n' + '='*60)
        self.stdout.write('Ð Ð•Ð—Ð£Ð›Ð¬Ð¢ÐÐ¢Ð« ÐŸÐ ÐžÐ’Ð•Ð ÐšÐ˜ API Ð”ÐžÐšÐ£ÐœÐ•ÐÐ¢ÐÐ¦Ð˜Ð˜')
        self.stdout.write('='*60)

        if undocumented_items:
            self.stdout.write(
                self.style.ERROR(
                    f'ERROR: ÐÐ°Ð¹Ð´ÐµÐ½Ð¾ {len(undocumented_items)} Ð¿Ñ€Ð¾Ð±Ð»ÐµÐ¼ Ñ Ð´Ð¾ÐºÑƒÐ¼ÐµÐ½Ñ‚Ð°Ñ†Ð¸ÐµÐ¹:'
                )
            )

            # Ð“Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€ÑƒÐµÐ¼ Ð¿Ñ€Ð¾Ð±Ð»ÐµÐ¼Ñ‹ Ð¿Ð¾ ViewSet
            issues_by_viewset = {}
            for item in undocumented_items:
                viewset = item['viewset']
                if viewset not in issues_by_viewset:
                    issues_by_viewset[viewset] = []
                issues_by_viewset[viewset].append(item)

            for viewset, issues in issues_by_viewset.items():
                self.stdout.write(f'\n{viewset}:')
                for issue in issues:
                    self.stdout.write(
                        f'  - {issue["method"]} ({issue["type"]}): {issue["issue"]}'
                    )
        else:
            self.stdout.write(
                self.style.SUCCESS(
                    'SUCCESS: Ð’ÑÐµ endpoints Ð¸Ð¼ÐµÑŽÑ‚ proper Ð´Ð¾ÐºÑƒÐ¼ÐµÐ½Ñ‚Ð°Ñ†Ð¸ÑŽ!'
                )
            )

        # Ð¡Ñ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ°
        self.stdout.write(f'\nÐ¡Ñ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ°:')
        self.stdout.write(f'  - Ð’ÑÐµÐ³Ð¾ Ð¼ÐµÑ‚Ð¾Ð´Ð¾Ð²: {total_methods}')
        self.stdout.write(f'  - Ð”Ð¾ÐºÑƒÐ¼ÐµÐ½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ñ…: {documented_methods}')
        self.stdout.write(f'  - ÐŸÐ¾ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ: {coverage_percent:.1f}%')

        if coverage_percent >= 90:
            style = self.style.SUCCESS
            prefix = 'EXCELLENT'
        elif coverage_percent >= 70:
            style = self.style.WARNING
            prefix = 'WARNING'
        else:
            style = self.style.ERROR
            prefix = 'POOR'

        self.stdout.write(
            style(f'  {prefix}: Ð£Ñ€Ð¾Ð²ÐµÐ½ÑŒ Ð´Ð¾ÐºÑƒÐ¼ÐµÐ½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ð¾ÑÑ‚Ð¸: {coverage_percent:.1f}%')
        )

        self.stdout.write('\n' + '='*60)

