    def filter_in_stock(self, queryset, name, value):
        """Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ Ð¿Ð¾ Ð½Ð°Ð»Ð¸Ñ‡Ð¸ÑŽ Ñ‚Ð¾Ð²Ð°Ñ€Ð° Ñ ÑƒÑ‡ÐµÑ‚Ð¾Ð¼ Ñ„Ð»Ð°Ð³Ð° is_active"""
