    def filter_search(self, queryset, name, value):
        """ÐŸÐ¾Ð»Ð½Ð¾Ñ‚ÐµÐºÑÑ‚Ð¾Ð²Ñ‹Ð¹ Ð¿Ð¾Ð¸ÑÐº Ñ Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ¾Ð¹ PostgreSQL FTS Ð¸ fallback Ð´Ð»Ñ Ð´Ñ€ÑƒÐ³Ð¸Ñ… Ð‘Ð”"""
        if not value:
            return queryset

        # Ð’Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ñ Ð´Ð»Ð¸Ð½Ñ‹ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ° Ð¸ Ð·Ð°Ñ‰Ð¸Ñ‚Ð° Ð¾Ñ‚ XSS
        search_query = value.strip()
        if len(search_query) > 100 or '<' in search_query or '>' in search_query:
            return queryset.none()

        if len(search_query) < 2:
            return queryset

        # ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ñ‚Ð¸Ð¿ Ð±Ð°Ð·Ñ‹ Ð´Ð°Ð½Ð½Ñ‹Ñ…
        from django.db import connection

        if connection.vendor == 'postgresql':
            # PostgreSQL full-text search Ñ Ñ€ÑƒÑÑÐºÐ¾ÑÐ·Ñ‹Ñ‡Ð½Ð¾Ð¹ ÐºÐ¾Ð½Ñ„Ð¸Ð³ÑƒÑ€Ð°Ñ†Ð¸ÐµÐ¹
            search_vector = (
                SearchVector('name', weight='A', config='russian') +
                SearchVector('short_description', weight='B', config='russian') +
                SearchVector('description', weight='C', config='russian') +
                SearchVector('sku', weight='A', config='russian')
            )

            search_query_obj = SearchQuery(search_query, config='russian')

            # Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Q-Ð¾Ð±ÑŠÐµÐºÑ‚ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ° Ð¿Ð¾ SKU Ñ‡ÐµÑ€ÐµÐ· icontains
            sku_q = Q(sku__icontains=search_query)

            # Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚Ñ‹ Ñ Ñ€Ð°Ð½Ð¶Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸ÐµÐ¼ Ð¿Ð¾ Ñ€ÐµÐ»ÐµÐ²Ð°Ð½Ñ‚Ð½Ð¾ÑÑ‚Ð¸
            return queryset.annotate(
                search=search_vector,
                rank=SearchRank(search_vector, search_query_obj)
            ).filter(
                Q(search=search_query_obj) | sku_q
            ).order_by('-rank', '-created_at')
        else:
            # Fallback Ð´Ð»Ñ SQLite Ð¸ Ð´Ñ€ÑƒÐ³Ð¸Ñ… Ð‘Ð” - Ð¿Ñ€Ð¾ÑÑ‚Ð¾Ð¹ icontains Ð¿Ð¾Ð¸ÑÐº Ñ Ð¿Ñ€Ð¸Ð¾Ñ€Ð¸Ñ‚Ð¸Ð·Ð°Ñ†Ð¸ÐµÐ¹
            from django.db.models import Case, When, Value, IntegerField

            # ÐŸÐ¾Ð¸ÑÐº Ñ‚Ð¾Ñ‡Ð½Ð¾Ð³Ð¾ ÑÐ¾Ð²Ð¿Ð°Ð´ÐµÐ½Ð¸Ñ Ð² Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ð¸ (Ð²Ñ‹ÑÑˆÐ¸Ð¹ Ð¿Ñ€Ð¸Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚)
            exact_name = queryset.filter(name__iexact=search_query)
            if exact_name.exists():
                return exact_name.order_by('-created_at')

            # ÐŸÐ¾Ð¸ÑÐº Ñ‡Ð°ÑÑ‚Ð¸Ñ‡Ð½Ð¾Ð³Ð¾ ÑÐ¾Ð²Ð¿Ð°Ð´ÐµÐ½Ð¸Ñ Ñ Ð¿Ñ€Ð¸Ð¾Ñ€Ð¸Ñ‚Ð¸Ð·Ð°Ñ†Ð¸ÐµÐ¹ Ð¿Ð¾ Ð¿Ð¾Ð»ÑÐ¼ (Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð¾Ð½ÐµÐ·Ð°Ð²Ð¸ÑÐ¸Ð¼Ñ‹Ð¹)
            name_match = Q(name__icontains=search_query)
            sku_match = Q(sku__icontains=search_query)  
            desc_match = Q(short_description__icontains=search_query) | Q(description__icontains=search_query)

            # ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€
            results = queryset.filter(name_match | sku_match | desc_match)

            # Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð°Ð½Ð½Ð¾Ñ‚Ð°Ñ†Ð¸ÑŽ Ð´Ð»Ñ Ð¿Ñ€Ð¸Ð¾Ñ€Ð¸Ñ‚Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð¸ ÑÐ¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼
            prioritized_results = results.annotate(
                priority=Case(
                    When(name_match, then=Value(1)),
                    When(sku_match, then=Value(2)),
                    When(desc_match, then=Value(3)),
                    default=Value(4),
                    output_field=IntegerField(),
                )
            ).order_by('priority', '-created_at')

            return prioritized_results

    def filter_size(self, queryset, name, value):
        """Ð¤Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ð¿Ð¾ Ñ€Ð°Ð·Ð¼ÐµÑ€Ñƒ Ð¸Ð· JSON Ð¿Ð¾Ð»Ñ specifications"""
        if not value:
            return queryset

        # ÐÐ¾Ñ€Ð¼Ð°Ð»Ð¸Ð·ÑƒÐµÐ¼ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ðµ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð°
        size_value = value.strip()
        if not size_value:
            return queryset

        # Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Q-Ð¾Ð±ÑŠÐµÐºÑ‚Ñ‹ Ð´Ð»Ñ Ñ€Ð°Ð·Ð»Ð¸Ñ‡Ð½Ñ‹Ñ… Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ð¾Ð² Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð° Ð² JSON
        size_queries = Q()

        # Ð’Ð°Ñ€Ð¸Ð°Ð½Ñ‚ 1: {"size": "XL"} - Ð¾Ð´Ð¸Ð½Ð¾Ñ‡Ð½Ñ‹Ð¹ Ñ€Ð°Ð·Ð¼ÐµÑ€
        size_queries |= Q(specifications__size=size_value)

        # Ð’Ð°Ñ€Ð¸Ð°Ð½Ñ‚ 2: {"sizes": ["M", "L", "XL"]} - Ð¼Ð°ÑÑÐ¸Ð² Ñ€Ð°Ð·Ð¼ÐµÑ€Ð¾Ð²
        size_queries |= Q(specifications__sizes__contains=[size_value])

        # Ð’Ð°Ñ€Ð¸Ð°Ð½Ñ‚ 3: {"Ñ€Ð°Ð·Ð¼ÐµÑ€": "XL"} - Ñ€ÑƒÑÑÐºÐ¸Ð¹ ÐºÐ»ÑŽÑ‡
        size_queries |= Q(specifications__Ñ€Ð°Ð·Ð¼ÐµÑ€=size_value)

        # Ð’Ð°Ñ€Ð¸Ð°Ð½Ñ‚ 4: {"Ñ€Ð°Ð·Ð¼ÐµÑ€Ñ‹": ["M", "L", "XL"]} - Ñ€ÑƒÑÑÐºÐ¸Ð¹ ÐºÐ»ÑŽÑ‡ Ð¼Ð°ÑÑÐ¸Ð²Ð°
        size_queries |= Q(specifications__Ñ€Ð°Ð·Ð¼ÐµÑ€Ñ‹__contains=[size_value])

        # Ð’Ð°Ñ€Ð¸Ð°Ð½Ñ‚ 5: Case-insensitive Ð¿Ð¾Ð¸ÑÐº Ð´Ð»Ñ ÑÑ‚Ñ€Ð¾ÐºÐ¾Ð²Ñ‹Ñ… Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ð¹
        from django.db import connection
        if connection.vendor == 'postgresql':
            # PostgreSQL Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶Ð¸Ð²Ð°ÐµÑ‚ case-insensitive Ð¿Ð¾Ð¸ÑÐº Ð² JSON
            size_queries |= Q(specifications__size__iexact=size_value)
            size_queries |= Q(specifications__Ñ€Ð°Ð·Ð¼ÐµÑ€__iexact=size_value)

        return queryset.filter(size_queries)

