    @extend_schema(
        summary="ÐŸÐµÑ€ÑÐ¾Ð½Ð°Ð»ÑŒÐ½Ñ‹Ð¹ Ð´Ð°ÑˆÐ±Ð¾Ñ€Ð´",
        description="ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð°Ð³Ñ€ÐµÐ³Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ð¾Ð¹ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¸ Ð´Ð»Ñ Ð»Ð¸Ñ‡Ð½Ð¾Ð³Ð¾ ÐºÐ°Ð±Ð¸Ð½ÐµÑ‚Ð° Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ",
        responses={200: UserDashboardSerializer},
        tags=["Users"],
    )
    def get(self, request):
        """ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð´Ð°ÑˆÐ±Ð¾Ñ€Ð´Ð°"""
        user = request.user

        # Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ ÑÑ‡ÐµÑ‚Ñ‡Ð¸ÐºÐ¸
        orders_count = 0  # TODO: Ð‘ÑƒÐ´ÐµÑ‚ Ñ€ÐµÐ°Ð»Ð¸Ð·Ð¾Ð²Ð°Ð½Ð¾ Ð¿Ð¾ÑÐ»Ðµ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Order Ð¼Ð¾Ð´ÐµÐ»Ð¸
        favorites_count = user.favorites.count()
        addresses_count = user.addresses.count()

        # Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ° Ð´Ð»Ñ B2B Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÐµÐ¹
        total_order_amount = None
        avg_order_amount = None
        verification_status = None
        if user.is_b2b_user:
            total_order_amount = 0  # TODO: Ð’Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ 0, Ð½ÑƒÐ¶Ð½Ð° Order Ð¼Ð¾Ð´ÐµÐ»ÑŒ
            avg_order_amount = 0  # TODO: Ð’Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ 0, Ð½ÑƒÐ¶Ð½Ð° Order Ð¼Ð¾Ð´ÐµÐ»ÑŒ
            verification_status = "verified" if user.is_verified else "pending"

