@pytest.mark.django_db class TestCartIntegration: """Ð˜Ð½Ñ‚ÐµÐ³Ñ€Ð°Ñ†Ð¸Ð¾Ð½Ð½Ñ‹Ðµ Ñ‚ÐµÑÑ‚Ñ‹ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹"""
def test_cart_workflow(self, user_factory, cart_factory, product_factory): """Ð¢ÐµÑÑ‚ Ð¿Ð¾Ð»Ð½Ð¾Ð³Ð¾ Ñ€Ð°Ð±Ð¾Ñ‡ÐµÐ³Ð¾ Ð¿Ñ€Ð¾Ñ†ÐµÑÑÐ° ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹""" user = user_factory.create() cart = cart_factory.create(user=user) product1 = product_factory.create( retail_price=Decimal('100.00'), stock_quantity=10, min_order_quantity=1 ) product2 = product_factory.create( retail_price=Decimal('200.00'), stock_quantity=10, min_order_quantity=1 )
