export interface ApiError {
  error: boolean;
  code: string;
  message: string;
  details: Record<string, any>;
  status: number;
}

export class ApiErrorHandler {
  static handle(error: any): ApiError {
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, ÑÐ²Ð»ÑÐµÑ‚ÑÑ Ð»Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐ° Ð½Ð°ÑˆÐ¸Ð¼ ÑÑ‚Ð°Ð½Ð´Ð°Ñ€Ñ‚Ð½Ñ‹Ð¼ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ð¾Ð¼
    if (error.response?.data?.error === true) {
      const apiError: ApiError = error.response.data;
      this.showUserNotification(apiError);
      return apiError;
    }

    // ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ network errors
    if (error.code === 'NETWORK_ERROR' || !error.response) {
      const networkError: ApiError = {
        error: true,
        code: 'NETWORK_ERROR',
        message: 'ÐŸÑ€Ð¾Ð±Ð»ÐµÐ¼Ñ‹ Ñ Ð¿Ð¾Ð´ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸ÐµÐ¼ Ðº Ð¸Ð½Ñ‚ÐµÑ€Ð½ÐµÑ‚Ñƒ',
        details: {},
        status: 0
      };
      this.showUserNotification(networkError);
      return networkError;
    }

    // ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ Ð¾ÑÑ‚Ð°Ð»ÑŒÐ½Ñ‹Ðµ HTTP Ð¾ÑˆÐ¸Ð±ÐºÐ¸
    const httpError: ApiError = {
      error: true,
      code: `HTTP_${error.response?.status || 'UNKNOWN'}`,
      message: this.getHttpErrorMessage(error.response?.status),
      details: error.response?.data || {},
      status: error.response?.status || 500
    };

    this.showUserNotification(httpError);
    return httpError;
  }

  private static getHttpErrorMessage(status?: number): string {
    const messages: Record<number, string> = {
      400: 'ÐÐµÐºÐ¾Ñ€Ñ€ÐµÐºÑ‚Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð² Ð·Ð°Ð¿Ñ€Ð¾ÑÐµ',
      401: 'ÐÐµÐ¾Ð±Ñ…Ð¾Ð´Ð¸Ð¼Ð¾ Ð²Ð¾Ð¹Ñ‚Ð¸ Ð² ÑÐ¸ÑÑ‚ÐµÐ¼Ñƒ',
      403: 'ÐÐµÐ´Ð¾ÑÑ‚Ð°Ñ‚Ð¾Ñ‡Ð½Ð¾ Ð¿Ñ€Ð°Ð² Ð´Ð»Ñ Ð²Ñ‹Ð¿Ð¾Ð»Ð½ÐµÐ½Ð¸Ñ Ð¾Ð¿ÐµÑ€Ð°Ñ†Ð¸Ð¸',
      404: 'Ð—Ð°Ð¿Ñ€Ð°ÑˆÐ¸Ð²Ð°ÐµÐ¼Ñ‹Ð¹ Ñ€ÐµÑÑƒÑ€Ñ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½',
      409: 'ÐšÐ¾Ð½Ñ„Ð»Ð¸ÐºÑ‚ Ð´Ð°Ð½Ð½Ñ‹Ñ…',
      422: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ð¸ Ð´Ð°Ð½Ð½Ñ‹Ñ…',
      429: 'Ð¡Ð»Ð¸ÑˆÐºÐ¾Ð¼ Ð¼Ð½Ð¾Ð³Ð¾ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ¾Ð², Ð¿Ð¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿Ð¾Ð·Ð¶Ðµ',
      500: 'Ð’Ð½ÑƒÑ‚Ñ€ÐµÐ½Ð½ÑÑ Ð¾ÑˆÐ¸Ð±ÐºÐ° ÑÐµÑ€Ð²ÐµÑ€Ð°',
      502: 'ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ð½ÐµÑˆÐ½ÐµÐ³Ð¾ ÑÐµÑ€Ð²Ð¸ÑÐ°',
      503: 'Ð¡ÐµÑ€Ð²Ð¸Ñ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿ÐµÐ½'
    };

    return messages[status || 500] || 'ÐŸÑ€Ð¾Ð¸Ð·Ð¾ÑˆÐ»Ð° Ð½ÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ð°Ñ Ð¾ÑˆÐ¸Ð±ÐºÐ°';
  }

  private static showUserNotification(error: ApiError) {
    // ÐžÐ¿Ñ€ÐµÐ´ÐµÐ»ÑÐµÐ¼ Ñ‚Ð¸Ð¿ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ Ð¿Ð¾ ÐºÐ¾Ð´Ñƒ Ð¾ÑˆÐ¸Ð±ÐºÐ¸
    const errorType = this.getNotificationType(error.code);

    // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ ÑÐ¾Ð¾Ñ‚Ð²ÐµÑ‚ÑÑ‚Ð²ÑƒÑŽÑ‰ÐµÐµ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ
    switch (errorType) {
      case 'error':
        toast.error(error.message);
        break;
      case 'warning':
        toast.warn(error.message);
        break;
      case 'info':
        toast.info(error.message);
        break;
      default:
        toast.error(error.message);
    }
  }

  private static getNotificationType(code: string): 'error' | 'warning' | 'info' {
    const warningCodes = ['INSUFFICIENT_STOCK', 'VALIDATION_ERROR'];
    const infoCodes = ['NETWORK_ERROR'];

    if (warningCodes.includes(code)) return 'warning';
    if (infoCodes.includes(code)) return 'info';
    return 'error';
  }
}

// Hook Ð´Ð»Ñ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¸ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð² ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°Ñ…
export function useApiErrorHandler() {
  return {
    handleError: (error: any) => ApiErrorHandler.handle(error),

    // ÐžÐ±ÐµÑ€Ñ‚ÐºÐ° Ð´Ð»Ñ async Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¹ Ñ Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ð¹ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¾Ð¹ Ð¾ÑˆÐ¸Ð±Ð¾Ðº
    withErrorHandling: <T extends any[], R>(
      fn: (...args: T) => Promise<R>
    ) => {
      return async (...args: T): Promise<R | null> => {
        try {
          return await fn(...args);
        } catch (error) {
          ApiErrorHandler.handle(error);
          return null;
        }
      };
    }
  };
}
