## 5. ÐœÐ¾Ð½Ð¸Ñ‚Ð¾Ñ€Ð¸Ð½Ð³ Ð¸ Ð°Ð»ÐµÑ€Ñ‚Ð¸Ð½Ð³ Ð¾ÑˆÐ¸Ð±Ð¾Ðº

### 5.1. Ð¡Ð¸ÑÑ‚ÐµÐ¼Ð° Ð°Ð³Ñ€ÐµÐ³Ð°Ñ†Ð¸Ð¸ Ð¾ÑˆÐ¸Ð±Ð¾Ðº

**Ð˜Ð½Ñ‚ÐµÐ»Ð»ÐµÐºÑ‚ÑƒÐ°Ð»ÑŒÐ½Ð°Ñ Ð³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²ÐºÐ° Ð¾ÑˆÐ¸Ð±Ð¾Ðº:**
```python
import hashlib
from collections import defaultdict

class ErrorAggregator:
    """
    ÐÐ³Ñ€ÐµÐ³Ð°Ñ†Ð¸Ñ Ð¸ Ð°Ð½Ð°Ð»Ð¸Ð· Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð´Ð»Ñ Ð²Ñ‹ÑÐ²Ð»ÐµÐ½Ð¸Ñ Ð¿Ð°Ñ‚Ñ‚ÐµÑ€Ð½Ð¾Ð²
    """

    def __init__(self):
        self.error_stats = defaultdict(lambda: defaultdict(int))
        self.alert_thresholds = {
            'error_rate': 0.05,      # 5% Ð¾Ñ‚ Ð¾Ð±Ñ‰ÐµÐ³Ð¾ Ñ‡Ð¸ÑÐ»Ð° Ð·Ð°Ð¿Ñ€Ð¾ÑÐ¾Ð²
            'unique_errors': 10,      # 10 ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ñ… Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð·Ð° Ð¿ÐµÑ€Ð¸Ð¾Ð´
            'critical_errors': 1      # ÐšÑ€Ð¸Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ðµ Ð¾ÑˆÐ¸Ð±ÐºÐ¸
        }

    def log_error(self, error: Exception, context: dict):
        """
        Ð›Ð¾Ð³Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð¸ Ð°Ð½Ð°Ð»Ð¸Ð· Ð¾ÑˆÐ¸Ð±ÐºÐ¸
        """
        error_hash = self._generate_error_hash(error, context)

        # Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð·Ð°Ð¿Ð¸ÑÑŒ Ð¾Ð± Ð¾ÑˆÐ¸Ð±ÐºÐµ
        error_record = ErrorRecord.objects.create(
            error_hash=error_hash,
            error_type=type(error).__name__,
            error_message=str(error),
            error_context=context,
            stack_trace=traceback.format_exc(),
            severity=self._determine_severity(error),
            is_resolved=False
        )

        # ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÑƒ
        self._update_error_stats(error_hash, error_record)

        # ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð½ÐµÐ¾Ð±Ñ…Ð¾Ð´Ð¸Ð¼Ð¾ÑÑ‚ÑŒ Ð°Ð»ÐµÑ€Ñ‚Ð°
        self._check_alert_conditions(error_record)

        return error_record

    def _generate_error_hash(self, error: Exception, context: dict) -> str:
        """
        Ð“ÐµÐ½ÐµÑ€Ð°Ñ†Ð¸Ñ Ñ…ÐµÑˆÐ° Ð´Ð»Ñ Ð³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²ÐºÐ¸ Ð¿Ð¾Ñ…Ð¾Ð¶Ð¸Ñ… Ð¾ÑˆÐ¸Ð±Ð¾Ðº
        """
        # ÐšÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ñ‹ Ð´Ð»Ñ Ñ…ÐµÑˆÐ¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
        error_signature = {
            'type': type(error).__name__,
            'message': str(error)[:100],  # ÐŸÐµÑ€Ð²Ñ‹Ðµ 100 ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¾Ð²
            'view': context.get('view', 'unknown'),
            'method': context.get('method', 'unknown')
        }

        signature_string = '|'.join(str(v) for v in error_signature.values())
        return hashlib.md5(signature_string.encode()).hexdigest()[:16]

    def _determine_severity(self, error: Exception) -> str:
        """
        ÐžÐ¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð¸Ðµ ÑƒÑ€Ð¾Ð²Ð½Ñ ÐºÑ€Ð¸Ñ‚Ð¸Ñ‡Ð½Ð¾ÑÑ‚Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐ¸
        """
        critical_exceptions = (
            OneCAuthenticationException,
            PaymentException,
            IntegrationException
        )

        warning_exceptions = (
            ValidationException,
            BusinessLogicException,
            InsufficientStockException
        )

        if isinstance(error, critical_exceptions):
            return 'critical'
        elif isinstance(error, warning_exceptions):
            return 'warning'
        else:
            return 'error'

    def _check_alert_conditions(self, error_record: ErrorRecord):
        """
        ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° ÑƒÑÐ»Ð¾Ð²Ð¸Ð¹ Ð´Ð»Ñ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ Ð°Ð»ÐµÑ€Ñ‚Ð¾Ð²
        """
        # ÐÐ»ÐµÑ€Ñ‚ Ð´Ð»Ñ ÐºÑ€Ð¸Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ñ… Ð¾ÑˆÐ¸Ð±Ð¾Ðº
        if error_record.severity == 'critical':
            self._send_critical_error_alert(error_record)

        # ÐÐ»ÐµÑ€Ñ‚ Ð´Ð»Ñ Ð²Ñ‹ÑÐ¾ÐºÐ¾Ð¹ Ñ‡Ð°ÑÑ‚Ð¾Ñ‚Ñ‹ Ð¾ÑˆÐ¸Ð±Ð¾Ðº
        recent_errors = ErrorRecord.objects.filter(
            error_hash=error_record.error_hash,
            created_at__gte=datetime.now() - timedelta(minutes=15)
        ).count()

        if recent_errors >= 5:  # 5 Ð¾Ð´Ð¸Ð½Ð°ÐºÐ¾Ð²Ñ‹Ñ… Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð·Ð° 15 Ð¼Ð¸Ð½ÑƒÑ‚
            self._send_high_frequency_alert(error_record, recent_errors)

    def get_error_dashboard_data(self) -> dict:
        """
        Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ dashboard Ð¼Ð¾Ð½Ð¸Ñ‚Ð¾Ñ€Ð¸Ð½Ð³Ð° Ð¾ÑˆÐ¸Ð±Ð¾Ðº
        """
        last_24h = datetime.now() - timedelta(hours=24)

        return {
            'total_errors': ErrorRecord.objects.filter(
                created_at__gte=last_24h
            ).count(),

            'unique_errors': ErrorRecord.objects.filter(
                created_at__gte=last_24h
            ).values('error_hash').distinct().count(),

            'critical_errors': ErrorRecord.objects.filter(
                created_at__gte=last_24h,
                severity='critical'
            ).count(),

            'top_errors': self._get_top_errors(last_24h),
            'error_trends': self._get_error_trends(),
            'resolution_stats': self._get_resolution_stats()
        }

    def _get_top_errors(self, since: datetime) -> list:
        """
        Ð¢Ð¾Ð¿ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð·Ð° Ð¿ÐµÑ€Ð¸Ð¾Ð´
        """
        from django.db.models import Count

        return list(
            ErrorRecord.objects.filter(created_at__gte=since)
            .values('error_hash', 'error_type', 'error_message')
            .annotate(count=Count('id'))
            .order_by('-count')[:10]
        )

class ErrorRecord(models.Model):
    """
    Ð—Ð°Ð¿Ð¸ÑÑŒ Ð¾Ð± Ð¾ÑˆÐ¸Ð±ÐºÐµ Ð² ÑÐ¸ÑÑ‚ÐµÐ¼Ðµ
    """
    error_hash = models.CharField(max_length=16, db_index=True)
    error_type = models.CharField(max_length=100)
    error_message = models.TextField()
    error_context = models.JSONField(default=dict)
    stack_trace = models.TextField()

    severity = models.CharField(max_length=20, choices=[
        ('info', 'Ð˜Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ð¾Ð½Ð½Ð°Ñ'),
        ('warning', 'ÐŸÑ€ÐµÐ´ÑƒÐ¿Ñ€ÐµÐ¶Ð´ÐµÐ½Ð¸Ðµ'),
