/**
 * React Component - Ð ÐµÐ°Ð»ÑŒÐ½Ñ‹Ð¹ Ð¿Ñ€Ð¸Ð¼ÐµÑ€ Ð¸Ð· Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð° FREESPORT
 * Ð”ÐµÐ¼Ð¾Ð½ÑÑ‚Ñ€Ð¸Ñ€ÑƒÐµÑ‚ Ð¿Ð°Ñ‚Ñ‚ÐµÑ€Ð½Ñ‹ TypeScript, Tailwind CSS, ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ loading
 */
import React from 'react';
import type { BaseComponentProps } from '@/types';

// âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: TypeScript Ð¸Ð½Ñ‚ÐµÑ€Ñ„ÐµÐ¹ÑÑ‹ Ð´Ð»Ñ props Ñ Ñ€Ð°ÑÑˆÐ¸Ñ€ÐµÐ½Ð¸ÐµÐ¼ Ð±Ð°Ð·Ð¾Ð²Ñ‹Ñ…
interface ButtonProps extends BaseComponentProps {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
  disabled?: boolean;
  loading?: boolean;
  type?: 'button' | 'submit' | 'reset';
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void;
}

/**
 * âœ… Ð Ð•ÐÐ›Ð¬ÐÐ«Ð™ ÐŸÐ Ð˜ÐœÐ•Ð : Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ð¹ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Button 
 * Ð˜Ð· frontend/src/components/ui/Button.tsx
 * 
 * ÐšÐ›Ð®Ð§Ð•Ð’Ð«Ð• ÐŸÐÐ¢Ð¢Ð•Ð ÐÐ«:
 * - TypeScript Ñ‚Ð¸Ð¿Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð²ÑÐµÑ… props
 * - Tailwind CSS Ð´Ð»Ñ ÑÑ‚Ð¸Ð»Ð¸Ð·Ð°Ñ†Ð¸Ð¸
 * - ÐŸÐ¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ° loading ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ñ ÑÐ¿Ð¸Ð½Ð½ÐµÑ€Ð¾Ð¼
 * - Spread Ð¾ÑÑ‚Ð°Ð»ÑŒÐ½Ñ‹Ñ… props (...props)
 * - Ð£ÑÐ»Ð¾Ð²Ð½Ð°Ñ ÑÐ±Ð¾Ñ€ÐºÐ° ÐºÐ»Ð°ÑÑÐ¾Ð²
 */
const Button: React.FC<ButtonProps> = ({
  children,
  className = '',
  variant = 'primary',
  size = 'md',
  disabled = false,
  loading = false,
  type = 'button',
  onClick,
  ...props  // âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: Spread Ð¾ÑÑ‚Ð°Ð»ÑŒÐ½Ñ‹Ñ… props
}) => {
  // âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ ÑÑ‚Ð¸Ð»Ð¸ Ñ Tailwind CSS
  const baseStyles = 'inline-flex items-center justify-center font-medium rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed';

  // âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: ÐžÐ±ÑŠÐµÐºÑ‚ Ñ Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ð°Ð¼Ð¸ ÑÑ‚Ð¸Ð»ÐµÐ¹
  const variantStyles = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 focus:ring-gray-500',
    outline: 'border border-gray-300 text-gray-700 bg-white hover:bg-gray-50 focus:ring-blue-500',
    ghost: 'text-gray-700 bg-transparent hover:bg-gray-100 focus:ring-gray-500',
    danger: 'bg-red-600 text-white hover:bg-red-700 focus:ring-red-500',
  };

  // âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: Ð Ð°Ð·Ð¼ÐµÑ€Ñ‹ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
  const sizeStyles = {
    sm: 'px-3 py-2 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-6 py-3 text-lg',
  };

  // âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: Ð”Ð¸Ð½Ð°Ð¼Ð¸Ñ‡ÐµÑÐºÐ°Ñ ÑÐ±Ð¾Ñ€ÐºÐ° ÐºÐ»Ð°ÑÑÐ¾Ð² Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸ÐµÐ¹
  const buttonClasses = [
    baseStyles,
    variantStyles[variant],
    sizeStyles[size],
    loading && 'cursor-wait',
    className,
  ].filter(Boolean).join(' ');

  return (
    <button
      type={type}
      className={buttonClasses}
      disabled={disabled || loading}
      onClick={onClick}
      {...props}
    >
      {/* âœ… ÐŸÐÐ¢Ð¢Ð•Ð Ð: SVG ÑÐ¿Ð¸Ð½Ð½ÐµÑ€ Ð´Ð»Ñ loading ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ */}
      {loading && (
        <svg
          className="-ml-1 mr-2 h-4 w-4 animate-spin"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
        >
          <circle
            className="opacity-25"
            cx="12"
            cy="12"
            r="10"
            stroke="currentColor"
            strokeWidth="4"
          />
          <path
            className="opacity-75"
            fill="currentColor"
            d="m4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
          />
        </svg>
      )}
      {children}
    </button>
  );
};

export default Button;

// âœ… ÐŸÐ Ð˜ÐœÐ•Ð : ÐšÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Ð¿Ñ€Ð¾Ð´ÑƒÐºÑ‚Ð° Ñ Ñ€Ð¾Ð»ÐµÐ²Ñ‹Ð¼Ð¸ Ñ†ÐµÐ½Ð°Ð¼Ð¸
interface ProductCardProps extends BaseComponentProps {
  product: {
    id: number;
    name: string;
    price: number;
    image: string;
    brand: string;
  };
  userRole?: string;
  onAddToCart?: (productId: number) => void;
}

const ProductCard: React.FC<ProductCardProps> = ({
  product,
  userRole = 'retail',
  onAddToCart,
  className = '',
}) => {
  const [loading, setLoading] = React.useState(false);

  const handleAddToCart = async () => {
    if (!onAddToCart) return;

    setLoading(true);
    try {
      await onAddToCart(product.id);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-200 ${className}`}>
      <img
        src={product.image}
        alt={product.name}
        className="w-full h-48 object-cover"
      />

      <div className="p-4">
        <h3 className="font-semibold text-lg mb-2">{product.name}</h3>
        <p className="text-gray-600 mb-2">{product.brand}</p>

        <div className="flex items-center justify-between">
          <span className="text-xl font-bold text-blue-600">
            {product.price.toLocaleString('ru-RU')} â‚½
          </span>

          <Button
            variant="primary"
            size="sm"
            loading={loading}
            onClick={handleAddToCart}
            disabled={!onAddToCart}
          >
            Ð’ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ
          </Button>
        </div>
      </div>
    </div>
  );
};

// âœ… Ð¨ÐÐ‘Ð›ÐžÐ ÐšÐžÐœÐŸÐžÐÐ•ÐÐ¢Ð Ð”Ð›Ð¯ ÐÐžÐ’Ð«Ð¥ Ð¡Ð£Ð©ÐÐžÐ¡Ð¢Ð•Ð™
