class CartService:
    """Ð¡ÐµÑ€Ð²Ð¸Ñ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ð¾Ð¹ Ñ Ð´ÐµÐ´ÑƒÐ¿Ð»Ð¸ÐºÐ°Ñ†Ð¸ÐµÐ¹"""

    def add_item(self, cart, product, quantity=1, user=None):
        """
        Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾Ð²Ð°Ñ€Ð° Ð² ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ Ñ Ð´ÐµÐ´ÑƒÐ¿Ð»Ð¸ÐºÐ°Ñ†Ð¸ÐµÐ¹

        Ð›ÐžÐ“Ð˜ÐšÐ:
        1. Ð•ÑÐ»Ð¸ Ñ‚Ð¾Ð²Ð°Ñ€ ÑƒÐ¶Ðµ ÐµÑÑ‚ÑŒ - ÑƒÐ²ÐµÐ»Ð¸Ñ‡Ð¸Ð²Ð°ÐµÐ¼ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾
        2. Ð•ÑÐ»Ð¸ Ñ‚Ð¾Ð²Ð°Ñ€Ð° Ð½ÐµÑ‚ - ÑÐ¾Ð·Ð´Ð°ÐµÐ¼ Ð½Ð¾Ð²ÑƒÑŽ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ
        3. Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ ÑÐ½Ð¸Ð¼Ð¾Ðº Ñ†ÐµÐ½Ñ‹ Ð½Ð° Ð¼Ð¾Ð¼ÐµÐ½Ñ‚ Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ñ
        """
        try:
            # ÐŸÐ¾Ð¿Ñ‹Ñ‚ÐºÐ° Ð½Ð°Ð¹Ñ‚Ð¸ ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÑŽÑ‰ÑƒÑŽ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ
            cart_item = CartItem.objects.get(cart=cart, product=product)

            # âœ… Ð”Ð•Ð”Ð£ÐŸÐ›Ð˜ÐšÐÐ¦Ð˜Ð¯: Ð£Ð²ÐµÐ»Ð¸Ñ‡Ð¸Ð²Ð°ÐµÐ¼ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾
            cart_item.quantity += quantity
            cart_item.save()

            return cart_item, False  # False = Ð½Ðµ ÑÐ¾Ð·Ð´Ð°Ð½ Ð½Ð¾Ð²Ñ‹Ð¹

        except CartItem.DoesNotExist:
            # âœ… Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð½Ð¾Ð²ÑƒÑŽ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ Ñ ÑÐ½Ð¸Ð¼ÐºÐ¾Ð¼ Ñ†ÐµÐ½Ñ‹
            current_price = product.get_price_for_user(user)

            cart_item = CartItem.objects.create(
                cart=cart,
                product=product, 
                quantity=quantity,
                price_snapshot=current_price
            )

            return cart_item, True  # True = ÑÐ¾Ð·Ð´Ð°Ð½ Ð½Ð¾Ð²Ñ‹Ð¹

    def update_item_quantity(self, cart, product, quantity):
        """
        ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð° Ñ‚Ð¾Ð²Ð°Ñ€Ð°

        Ð›ÐžÐ“Ð˜ÐšÐ:
        - quantity = 0 â†’ ÑƒÐ´Ð°Ð»ÑÐµÐ¼ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ
        - quantity > 0 â†’ Ð¾Ð±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾
        """
        try:
            cart_item = CartItem.objects.get(cart=cart, product=product)

            if quantity <= 0:
                cart_item.delete()
                return None
            else:
                cart_item.quantity = quantity
                cart_item.save()
                return cart_item

        except CartItem.DoesNotExist:
            if quantity > 0:
                # Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð½Ð¾Ð²ÑƒÑŽ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑŽ ÐµÑÐ»Ð¸ ÐµÑ‘ Ð½Ðµ Ð±Ñ‹Ð»Ð¾
                return self.add_item(cart, product, quantity)[0]
            return None

    def merge_carts(self, anonymous_cart, user_cart):
        """
        Ð¡Ð»Ð¸ÑÐ½Ð¸Ðµ Ð°Ð½Ð¾Ð½Ð¸Ð¼Ð½Ð¾Ð¹ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹ Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒÑÐºÐ¾Ð¹
        ÐŸÑ€Ð¸ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
        """
        for item in anonymous_cart.items.all():
            self.add_item(
                cart=user_cart,
                product=item.product,
                quantity=item.quantity,
                user=user_cart.user
            )

        # Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð°Ð½Ð¾Ð½Ð¸Ð¼Ð½ÑƒÑŽ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ
        anonymous_cart.delete()
```

