class CartItemViewSet(viewsets.ModelViewSet):
    """API Ð´Ð»Ñ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ Ð¿Ð¾Ð·Ð¸Ñ†Ð¸ÑÐ¼Ð¸ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹"""

    permission_classes = [permissions.AllowAny]  # ÐŸÐ¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ° Ð°Ð½Ð¾Ð½Ð¸Ð¼Ð½Ñ‹Ñ… ÐºÐ¾Ñ€Ð·Ð¸Ð½

    def get_queryset(self):
        cart = self.get_or_create_cart()
        return CartItem.objects.filter(cart=cart).select_related('product', 'product__brand')

    def get_or_create_cart(self):
        """ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð¸Ð»Ð¸ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ðµ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ/ÑÐµÑÑÐ¸Ð¸"""
        if self.request.user.is_authenticated:
            cart, created = Cart.objects.get_or_create(user=self.request.user)
        else:
            session_key = self.request.session.session_key
            if not session_key:
                self.request.session.create()
                session_key = self.request.session.session_key
            cart, created = Cart.objects.get_or_create(session_key=session_key)

        return cart

    @action(detail=False, methods=['post'])
    def add_item(self, request):
        """
        Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾Ð²Ð°Ñ€Ð° Ð² ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ
        POST /api/cart/add_item/
        {
            "product_id": 123,
            "quantity": 2
        }
        """
        product_id = request.data.get('product_id')
        quantity = int(request.data.get('quantity', 1))

        try:
            product = Product.objects.get(id=product_id, is_active=True)
            cart = self.get_or_create_cart()

            cart_service = CartService()
            cart_item, created = cart_service.add_item(
                cart=cart,
                product=product,
                quantity=quantity,
                user=request.user if request.user.is_authenticated else None
            )

            serializer = CartItemSerializer(cart_item)

            return Response({
                'item': serializer.data,
                'created': created,
                'total_items': cart.items.count(),
                'cart_total': cart.get_total_price()
            })

        except Product.DoesNotExist:
            return Response(
                {'error': 'Ð¢Ð¾Ð²Ð°Ñ€ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½'}, 
                status=status.HTTP_404_NOT_FOUND
            )

    @action(detail=True, methods=['patch'])
    def update_quantity(self, request, pk=None):
        """
        ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð° Ñ‚Ð¾Ð²Ð°Ñ€Ð°
        PATCH /api/cart/{product_id}/update_quantity/
        {
            "quantity": 5
        }
        """
        quantity = int(request.data.get('quantity', 1))
        cart = self.get_or_create_cart()

        try:
            product = Product.objects.get(id=pk)
            cart_service = CartService()

            cart_item = cart_service.update_item_quantity(cart, product, quantity)

            if cart_item:
                serializer = CartItemSerializer(cart_item)
                return Response(serializer.data)
            else:
                return Response({'message': 'Ð¢Ð¾Ð²Ð°Ñ€ ÑƒÐ´Ð°Ð»ÐµÐ½ Ð¸Ð· ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹'})

        except Product.DoesNotExist:
            return Response(
                {'error': 'Ð¢Ð¾Ð²Ð°Ñ€ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½'}, 
                status=status.HTTP_404_NOT_FOUND
            )
