## Frontend Ð¸Ð½Ñ‚ÐµÐ³Ñ€Ð°Ñ†Ð¸Ñ

### React Auth Hook

```typescript
interface User {
  id: number;
  email: string;
  role: string;
  full_name: string;
  is_verified: boolean;
}

interface AuthState {
  user: User | null;
  access_token: string | null;
  refresh_token: string | null;
  loading: boolean;
}

const useAuth = () => {
  const [authState, setAuthState] = useState<AuthState>({
    user: null,
    access_token: localStorage.getItem('access_token'),
    refresh_token: localStorage.getItem('refresh_token'),
    loading: false,
  });

  const login = async (email: string, password: string) => {
    setAuthState(prev => ({ ...prev, loading: true }));

    try {
      const response = await fetch('/api/auth/login/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();

      if (response.ok) {
        // âœ… Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ñ‚Ð¾ÐºÐµÐ½Ñ‹ Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
        localStorage.setItem('access_token', data.access);
        localStorage.setItem('refresh_token', data.refresh);

        setAuthState({
          user: data.user,
          access_token: data.access,
          refresh_token: data.refresh,
          loading: false,
        });

        return { success: true };
      } else {
        return { success: false, errors: data };
      }
    } catch (error) {
      return { success: false, errors: { non_field_errors: ['ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐµÑ‚Ð¸'] } };
    } finally {
      setAuthState(prev => ({ ...prev, loading: false }));
    }
  };

  const register = async (userData: RegisterData) => {
    setAuthState(prev => ({ ...prev, loading: true }));

    try {
      const response = await fetch('/api/auth/register/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData)
      });

      const data = await response.json();

      return { success: response.ok, data, errors: response.ok ? null : data };
    } finally {
      setAuthState(prev => ({ ...prev, loading: false }));
    }
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setAuthState({
      user: null,
      access_token: null,
      refresh_token: null,
      loading: false,
    });
  };

  return { ...authState, login, register, logout };
};
```

