/**
 * Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ð¹ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Button Ñ TypeScript Ð¸ Tailwind CSS
 * ÐŸÐ¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ° Ñ€Ð°Ð·Ð»Ð¸Ñ‡Ð½Ñ‹Ñ… Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ð¾Ð² Ð¸ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ð¹
 */
import React from 'react';
import type { BaseComponentProps } from '@/types';

interface ButtonProps extends BaseComponentProps {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
  disabled?: boolean;
  loading?: boolean;
  type?: 'button' | 'submit' | 'reset';
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void;
}

const Button: React.FC<ButtonProps> = ({
  children,
  className = '',
  variant = 'primary',
  size = 'md',
  disabled = false,
  loading = false,
  type = 'button',
  onClick,
  ...props
}) => {
  // Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ðµ ÑÑ‚Ð¸Ð»Ð¸
  const baseStyles = 'inline-flex items-center justify-center font-medium rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed';

  // Ð¡Ñ‚Ð¸Ð»Ð¸ Ð²Ð°Ñ€Ð¸Ð°Ð½Ñ‚Ð¾Ð²
  const variantStyles = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 focus:ring-gray-500',
    outline: 'border border-gray-300 text-gray-700 bg-white hover:bg-gray-50 focus:ring-blue-500',
    ghost: 'text-gray-700 bg-transparent hover:bg-gray-100 focus:ring-gray-500',
    danger: 'bg-red-600 text-white hover:bg-red-700 focus:ring-red-500',
  };

  // Ð Ð°Ð·Ð¼ÐµÑ€Ñ‹
  const sizeStyles = {
    sm: 'px-3 py-2 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-6 py-3 text-lg',
  };

  // ÐžÐ±ÑŠÐµÐ´Ð¸Ð½ÐµÐ½Ð¸Ðµ Ð²ÑÐµÑ… ÑÑ‚Ð¸Ð»ÐµÐ¹
  const buttonClasses = [
    baseStyles,
    variantStyles[variant],
    sizeStyles[size],
    loading && 'cursor-wait',
    className,
  ].filter(Boolean).join(' ');

  return (
    <button
      type={type}
      className={buttonClasses}
      disabled={disabled || loading}
      onClick={onClick}
      {...props}
    >
      {loading && (
        <svg
          className="-ml-1 mr-2 h-4 w-4 animate-spin"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
        >
          <circle
            className="opacity-25"
            cx="12"
            cy="12"
            r="10"
            stroke="currentColor"
            strokeWidth="4"
          />
          <path
            className="opacity-75"
            fill="currentColor"
            d="m4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
          />
        </svg>
      )}
      {children}
    </button>
  );
};

export default Button;

/**
 * Barrel export Ð´Ð»Ñ UI ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð¾Ð²
 * Ð£Ð¿Ñ€Ð¾Ñ‰Ð°ÐµÑ‚ Ð¸Ð¼Ð¿Ð¾Ñ€Ñ‚Ñ‹ Ð² Ð´Ñ€ÑƒÐ³Ð¸Ñ… Ñ‡Ð°ÑÑ‚ÑÑ… Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ñ
 */

export { default as Button } from './Button';

// TODO: Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÑÐºÑÐ¿Ð¾Ñ€Ñ‚Ñ‹ Ð´Ñ€ÑƒÐ³Ð¸Ñ… UI ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð¾Ð² Ð¿Ð¾ Ð¼ÐµÑ€Ðµ Ð¸Ñ… ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ
// export { default as Input } from './Input';
// export { default as Modal } from './Modal';
// export { default as Loading } from './Loading';
// export { default as Card } from './Card';

/**
 * API ÐºÐ»Ð¸ÐµÐ½Ñ‚ Ð´Ð»Ñ Ð²Ð·Ð°Ð¸Ð¼Ð¾Ð´ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ Ñ Django backend
 * ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ° Axios Ñ JWT Ñ‚Ð¾ÐºÐµÐ½Ð°Ð¼Ð¸ Ð¸ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¾Ð¹ Ð¾ÑˆÐ¸Ð±Ð¾Ðº
 */
import axios from 'axios';
import type { AuthTokens, ApiResponse } from '@/types';

// ÐšÐ¾Ð½Ñ„Ð¸Ð³ÑƒÑ€Ð°Ñ†Ð¸Ñ API
const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8001/api/v1';

// Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Axios Ð¸Ð½ÑÑ‚Ð°Ð½ÑÐ°
export const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾ÐºÐµÐ½Ð°Ð¼Ð¸ Ð² localStorage
export const tokenStorage = {
  get(): AuthTokens | null {
    if (typeof window === 'undefined') return null;

    const accessToken = localStorage.getItem('access_token');
    const refreshToken = localStorage.getItem('refresh_token');

    if (accessToken && refreshToken) {
      return {
        access: accessToken,
        refresh: refreshToken,
      };
    }

    return null;
  },

  set(tokens: AuthTokens): void {
    if (typeof window === 'undefined') return;

    localStorage.setItem('access_token', tokens.access);
    localStorage.setItem('refresh_token', tokens.refresh);
  },

  clear(): void {
    if (typeof window === 'undefined') return;

    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
  },
};

// Request interceptor Ð´Ð»Ñ Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ñ JWT Ñ‚Ð¾ÐºÐµÐ½Ð°
apiClient.interceptors.request.use(
  (config) => {
    const tokens = tokenStorage.get();

    if (tokens?.access) {
      config.headers = config.headers || {};
      config.headers.Authorization = `Bearer ${tokens.access}`;
    }

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor Ð´Ð»Ñ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ¸ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð¸ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð²
apiClient.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    const originalRequest = error.config;

    // Ð•ÑÐ»Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ð»Ð¸ 401 Ð¸ ÑÑ‚Ð¾ Ð½Ðµ Ð¿Ð¾Ð²Ñ‚Ð¾Ñ€Ð½Ñ‹Ð¹ Ð·Ð°Ð¿Ñ€Ð¾Ñ
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      const tokens = tokenStorage.get();

      if (tokens?.refresh) {
        try {
          // ÐŸÐ¾Ð¿Ñ‹Ñ‚ÐºÐ° Ð¾Ð±Ð½Ð¾Ð²Ð¸Ñ‚ÑŒ access token
          const response = await axios.post(`${API_BASE_URL}/auth/token/refresh/`, {
            refresh: tokens.refresh,
          });

          const newTokens = response.data as AuthTokens;
          tokenStorage.set(newTokens);

          // ÐŸÐ¾Ð²Ñ‚Ð¾Ñ€Ð¸Ñ‚ÑŒ Ð¾Ñ€Ð¸Ð³Ð¸Ð½Ð°Ð»ÑŒÐ½Ñ‹Ð¹ Ð·Ð°Ð¿Ñ€Ð¾Ñ Ñ Ð½Ð¾Ð²Ñ‹Ð¼ Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð¼
          originalRequest.headers = originalRequest.headers || {};
          originalRequest.headers.Authorization = `Bearer ${newTokens.access}`;
          return apiClient(originalRequest);
        } catch (refreshError) {
          // Refresh Ñ‚Ð¾ÐºÐµÐ½ Ð½ÐµÐ´ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ‚ÐµÐ»ÐµÐ½, Ð¾Ñ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð¸ Ð¿ÐµÑ€ÐµÐ½Ð°Ð¿Ñ€Ð°Ð²Ð»ÑÐµÐ¼ Ð½Ð° Ð»Ð¾Ð³Ð¸Ð½
          tokenStorage.clear();

          if (typeof window !== 'undefined') {
            window.location.href = '/auth/login';
          }

          return Promise.reject(refreshError);
        }
      }
    }

    return Promise.reject(error);
  }
);

// Ð¢Ð¸Ð¿Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ðµ API Ð¼ÐµÑ‚Ð¾Ð´Ñ‹
export const api = {
  // GET Ð·Ð°Ð¿Ñ€Ð¾Ñ
  async get<T>(url: string): Promise<ApiResponse<T>> {
    try {
      const response = await apiClient.get<ApiResponse<T>>(url);
      return response.data;
    } catch (error) {
      throw this.handleError(error);
    }
  },

  // POST Ð·Ð°Ð¿Ñ€Ð¾Ñ
  async post<T>(url: string, data?: unknown): Promise<ApiResponse<T>> {
    try {
      const response = await apiClient.post<ApiResponse<T>>(url, data);
      return response.data;
    } catch (error) {
      throw this.handleError(error);
    }
  },

  // PUT Ð·Ð°Ð¿Ñ€Ð¾Ñ
  async put<T>(url: string, data?: unknown): Promise<ApiResponse<T>> {
    try {
      const response = await apiClient.put<ApiResponse<T>>(url, data);
      return response.data;
    } catch (error) {
      throw this.handleError(error);
    }
  },

  // DELETE Ð·Ð°Ð¿Ñ€Ð¾Ñ
  async delete<T>(url: string): Promise<ApiResponse<T>> {
    try {
      const response = await apiClient.delete<ApiResponse<T>>(url);
      return response.data;
    } catch (error) {
      throw this.handleError(error);
    }
  },

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¾ÑˆÐ¸Ð±Ð¾Ðº
  handleError(error: unknown): Error {
    const axiosError = error as { response?: { data?: { message?: string; errors?: Record<string, string[]> } }; message?: string };

    if (axiosError.response?.data?.message) {
      return new Error(axiosError.response.data.message);
    }

    if (axiosError.response?.data?.errors) {
      const firstError = Object.values(axiosError.response.data.errors)[0];
      if (Array.isArray(firstError) && firstError.length > 0) {
        return new Error(firstError[0]);
      }
    }

    return new Error(axiosError.message || 'ÐŸÑ€Ð¾Ð¸Ð·Ð¾ÑˆÐ»Ð° Ð½ÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ð°Ñ Ð¾ÑˆÐ¸Ð±ÐºÐ°');
  },
};

/**
 * Zustand store Ð´Ð»Ñ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸ÐµÐ¹
 * Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð¸ JWT Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð²
 */
import { create } from 'zustand';
import { devtools } from 'zustand/middleware';
import type { User, AuthTokens } from '@/types';
import { tokenStorage } from '@/services/api';

interface AuthState {
  user: User | null;
  tokens: AuthTokens | null;
  isAuthenticated: boolean;
  isLoading: boolean;
}

interface AuthActions {
  // ÐÐ²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  login: (user: User, tokens: AuthTokens) => void;

  // Ð’Ñ‹Ñ…Ð¾Ð´ Ð¸Ð· ÑÐ¸ÑÑ‚ÐµÐ¼Ñ‹
  logout: () => void;

  // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  updateUser: (user: Partial<User>) => void;

  // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð²
  updateTokens: (tokens: AuthTokens) => void;

  // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð¸Ð· localStorage
  initializeAuth: () => void;

  // Ð£ÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
  setLoading: (loading: boolean) => void;
}

type AuthStore = AuthState & AuthActions;

// ÐÐ°Ñ‡Ð°Ð»ÑŒÐ½Ð¾Ðµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ
const initialState: AuthState = {
  user: null,
  tokens: null,
  isAuthenticated: false,
  isLoading: true,
};

// Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Zustand store
export const useAuthStore = create<AuthStore>()(
  devtools(
    (set, get) => ({
      ...initialState,

      login: (user: User, tokens: AuthTokens) => {
        // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð² Ð² localStorage
        tokenStorage.set(tokens);

        set({
          user,
          tokens,
          isAuthenticated: true,
          isLoading: false,
        }, false, 'auth/login');
      },

      logout: () => {
        // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° localStorage
        tokenStorage.clear();

        set({
          user: null,
          tokens: null,
          isAuthenticated: false,
          isLoading: false,
        }, false, 'auth/logout');
      },

      updateUser: (userData: Partial<User>) => {
        const { user } = get();

        if (user) {
          set({
            user: { ...user, ...userData },
          }, false, 'auth/updateUser');
        }
      },

      updateTokens: (tokens: AuthTokens) => {
        // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾ÐºÐµÐ½Ð¾Ð² Ð² localStorage
        tokenStorage.set(tokens);

        set({
          tokens,
        }, false, 'auth/updateTokens');
      },

      initializeAuth: () => {
        try {
          const storedTokens = tokenStorage.get();

          if (storedTokens) {
            set({
              tokens: storedTokens,
              isAuthenticated: true,
              isLoading: false,
            }, false, 'auth/initialize');

            // TODO: Ð—Ð´ÐµÑÑŒ Ð¼Ð¾Ð¶Ð½Ð¾ Ð´Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð·Ð°Ð¿Ñ€Ð¾Ñ Ð´Ð»Ñ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
            // Ð¸Ð· backend Ð¿Ð¾ Ñ‚Ð¾ÐºÐµÐ½Ñƒ
          } else {
            set({
              isLoading: false,
            }, false, 'auth/initialize');
          }
        } catch (error) {
          console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¸Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸:', error);

          set({
            user: null,
            tokens: null,
            isAuthenticated: false,
            isLoading: false,
          }, false, 'auth/initializeError');
        }
      },

      setLoading: (loading: boolean) => {
        set({
          isLoading: loading,
        }, false, 'auth/setLoading');
      },
    }),
    {
      name: 'auth-store',
      // Ð¡ÐµÑ€Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð¿ÑƒÐ±Ð»Ð¸Ñ‡Ð½Ñ‹Ñ… Ð¿Ð¾Ð»ÐµÐ¹ Ð´Ð»Ñ devtools
      serialize: {
        options: {
          user: true,
          isAuthenticated: true,
          isLoading: true,
          // ÐÐµ ÑÐµÑ€Ð¸Ð°Ð»Ð¸Ð·ÑƒÐµÐ¼ Ñ‚Ð¾ÐºÐµÐ½Ñ‹ Ð´Ð»Ñ Ð±ÐµÐ·Ð¾Ð¿Ð°ÑÐ½Ð¾ÑÑ‚Ð¸
          tokens: false,
        },
      },
    }
  )
);

// Ð¡ÐµÐ»ÐµÐºÑ‚Ð¾Ñ€Ñ‹ Ð´Ð»Ñ ÑƒÐ´Ð¾Ð±Ð½Ð¾Ð³Ð¾ Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð° Ðº ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸ÑŽ
export const authSelectors = {
  // Ð¢ÐµÐºÑƒÑ‰Ð¸Ð¹ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ
  useUser: () => useAuthStore((state) => state.user),

  // Ð¡Ñ‚Ð°Ñ‚ÑƒÑ Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸
  useIsAuthenticated: () => useAuthStore((state) => state.isAuthenticated),

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
  useIsLoading: () => useAuthStore((state) => state.isLoading),

  // Ð Ð¾Ð»ÑŒ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  useUserRole: () => useAuthStore((state) => state.user?.role),

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ñ€Ð¾Ð»Ð¸ B2B
  useIsB2BUser: () => useAuthStore((state) => {
    const role = state.user?.role;
    return role ? ['wholesale_level1', 'wholesale_level2', 'wholesale_level3', 'trainer', 'federation_rep'].includes(role) : false;
  }),

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ñ€Ð¾Ð»Ð¸ Ð°Ð´Ð¼Ð¸Ð½Ð°
  useIsAdmin: () => useAuthStore((state) => state.user?.role === 'admin'),
};

/**
 * Ð¢Ð¸Ð¿Ñ‹ TypeScript Ð´Ð»Ñ FREESPORT Platform
 * Ð˜Ð½Ñ‚ÐµÐ³Ñ€Ð°Ñ†Ð¸Ñ Ñ Django backend API
 */

// Ð¢Ð¸Ð¿Ñ‹ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÐµÐ¹ Ð¸ Ð°ÑƒÑ‚ÐµÐ½Ñ‚Ð¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸
export interface User {
  id: number;
  email: string;
  firstName: string;
  lastName: string;
  role: UserRole;
  phone?: string;
  companyName?: string;
  taxId?: string;
  isVerified: boolean;
  createdAt: string;
  updatedAt: string;
}

export type UserRole = 
  | 'retail'
  | 'wholesale_level1' 
  | 'wholesale_level2'
  | 'wholesale_level3'
  | 'trainer'
  | 'federation_rep'
  | 'admin';

export interface AuthTokens {
  access: string;
  refresh: string;
}

export interface AuthResponse {
  user: User;
  tokens: AuthTokens;
}

// Ð¢Ð¸Ð¿Ñ‹ API Ð¾Ñ‚Ð²ÐµÑ‚Ð¾Ð²
export interface ApiResponse<T = unknown> {
  success: boolean;
  data?: T;
  message?: string;
  errors?: Record<string, string[]>;
}

export interface PaginatedResponse<T> {
  count: number;
  next: string | null;
  previous: string | null;
  results: T[];
}

